import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FaExternalLinkAlt, FaGithub, FaTimes, FaReact, FaNodeJs, FaPython, FaJava, FaDatabase } from 'react-icons/fa';
import { SiMongodb, SiExpress, SiPostgresql, SiSpringboot, SiFlutter, SiDart, SiTensorflow } from 'react-icons/si';

const Projects = () => {
  const [selectedProject, setSelectedProject] = useState(null);

  const projects = [
    {
      id: 1,
      title: "MarketPulse Pro",
      description: "Advanced stock market analysis platform with real-time data visualization",
      fullDescription: "MarketPulse Pro is a comprehensive stock market analysis platform that provides real-time market data, advanced charting capabilities, and portfolio management tools. Built with React and integrated with multiple financial APIs, it offers users professional-grade analytics for informed trading decisions.",
      image: "project1-placeholder.jpg",
      technologies: ["React", "Node.js", "MongoDB", "Express", "Chart.js", "WebSocket"],
      icons: [<FaReact />, <FaNodeJs />, <SiMongodb />, <SiExpress />],
      github: "https://github.com/Prasannaram2k4",
      demo: "https://project-demo.com",
      features: [
        "Real-time stock price updates",
        "Advanced charting and technical analysis",
        "Portfolio tracking and management",
        "Market news integration",
        "Custom watchlists and alerts"
      ],
      category: "Full Stack Web"
    },
    {
      id: 2,
      title: "EcoTrack Mobile App",
      description: "Flutter-based environmental impact tracking application",
      fullDescription: "EcoTrack is a mobile application designed to help users monitor and reduce their environmental footprint. The app tracks daily activities, calculates carbon emissions, and provides personalized recommendations for sustainable living practices.",
      image: "project2-placeholder.jpg",
      technologies: ["Flutter", "Dart", "Firebase", "Google Maps API"],
      icons: [<SiFlutter />, <SiDart />, <FaDatabase />],
      github: "https://github.com/Prasannaram2k4",
      demo: "https://project-demo.com",
      features: [
        "Carbon footprint calculator",
        "Daily activity tracking",
        "Sustainable lifestyle tips",
        "Progress visualization",
        "Community challenges"
      ],
      category: "Mobile Development"
    },
    {
      id: 3,
      title: "AI Recipe Generator",
      description: "Machine learning powered recipe recommendation system",
      fullDescription: "An intelligent recipe recommendation system that uses machine learning algorithms to suggest personalized recipes based on user preferences, dietary restrictions, and available ingredients. The system learns from user interactions to improve recommendations over time.",
      image: "project3-placeholder.jpg",
      technologies: ["Python", "TensorFlow", "Flask", "React", "PostgreSQL"],
      icons: [<FaPython />, <SiTensorflow />, <FaReact />, <SiPostgresql />],
      github: "https://github.com/Prasannaram2k4",
      demo: "https://project-demo.com",
      features: [
        "Personalized recipe recommendations",
        "Ingredient-based recipe search",
        "Nutritional analysis",
        "Meal planning assistant",
        "User preference learning"
      ],
      category: "AI/ML"
    },
    {
      id: 4,
      title: "TaskMaster Enterprise",
      description: "Comprehensive project management and team collaboration platform",
      fullDescription: "TaskMaster Enterprise is a robust project management solution designed for teams and organizations. It features advanced project tracking, resource management, team collaboration tools, and comprehensive reporting capabilities to streamline workflow and boost productivity.",
      image: "project4-placeholder.jpg",
      technologies: ["Java", "Spring Boot", "React", "PostgreSQL", "Redis"],
      icons: [<FaJava />, <SiSpringboot />, <FaReact />, <SiPostgresql />],
      github: "https://github.com/Prasannaram2k4",
      demo: "https://project-demo.com",
      features: [
        "Advanced project planning and tracking",
        "Resource allocation and management",
        "Team collaboration tools",
        "Real-time progress monitoring",
        "Comprehensive reporting and analytics"
      ],
      category: "Enterprise Software"
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.3
      }
    }
  };

  const cardVariants = {
    hidden: { 
      opacity: 0,
      y: 50,
      scale: 0.9
    },
    visible: {
      opacity: 1,
      y: 0,
      scale: 1,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    },
    hover: {
      y: -10,
      scale: 1.02,
      boxShadow: "0 20px 40px rgba(255,255,255,0.1)",
      transition: {
        duration: 0.3,
        ease: "easeInOut"
      }
    }
  };

  const modalVariants = {
    hidden: {
      opacity: 0,
      scale: 0.8,
      y: 100
    },
    visible: {
      opacity: 1,
      scale: 1,
      y: 0,
      transition: {
        duration: 0.5,
        ease: "easeOut"
      }
    },
    exit: {
      opacity: 0,
      scale: 0.8,
      y: 100,
      transition: {
        duration: 0.3
      }
    }
  };

  const overlayVariants = {
    hidden: { opacity: 0 },
    visible: { opacity: 1 },
    exit: { opacity: 0 }
  };

  return (
    <motion.section 
      id="projects" 
      className="section-padding bg-gradient-to-br from-gray-900 to-black relative overflow-hidden"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: false, amount: 0.1 }}
      variants={containerVariants}
    >
      {/* Background Animation */}
      <motion.div 
        className="absolute inset-0 -z-10"
        animate={{
          background: [
            "radial-gradient(circle at 30% 20%, rgba(255,255,255,0.02) 0%, transparent 50%)",
            "radial-gradient(circle at 70% 80%, rgba(255,255,255,0.02) 0%, transparent 50%)",
            "radial-gradient(circle at 20% 70%, rgba(255,255,255,0.02) 0%, transparent 50%)",
          ]
        }}
        transition={{ duration: 10, repeat: Infinity, repeatType: "reverse" }}
      />

      <div className="container mx-auto relative z-10">
        <motion.div className="text-center mb-16">
          <motion.h2 
            className="text-4xl md:text-5xl font-bold mb-6 text-primary"
            variants={cardVariants}
          >
            Featured Projects
          </motion.h2>
          <motion.div
            className="w-24 h-1 bg-gradient-to-r from-transparent via-primary to-transparent mx-auto mb-8"
            initial={{ width: 0 }}
            whileInView={{ width: 96 }}
            viewport={{ once: false, amount: 0.3 }}
            transition={{ duration: 1, delay: 0.5 }}
          />
          <motion.p
            className="text-secondary text-lg max-w-2xl mx-auto"
            variants={cardVariants}
          >
            Explore my latest projects showcasing modern web development, 
            mobile applications, and innovative solutions.
          </motion.p>
        </motion.div>

        {/* Horizontal Project Cards */}
        <div className="space-y-8 max-w-6xl mx-auto">
          {projects.map((project, index) => (
            <motion.div
              key={project.id}
              variants={cardVariants}
              whileHover="hover"
              className={`group cursor-pointer ${
                index % 2 === 0 ? 'lg:flex-row' : 'lg:flex-row-reverse'
              } flex flex-col lg:flex-row bg-gradient-to-br from-gray-800/40 to-gray-900/40 backdrop-blur-sm rounded-2xl overflow-hidden border border-primary/20 hover:border-primary/40 transition-all duration-500`}
              onClick={() => setSelectedProject(project)}
            >
              {/* Project Image */}
              <div className="lg:w-1/2 h-64 lg:h-80 bg-gradient-to-br from-primary/20 to-gray-700/20 relative overflow-hidden">
                <motion.div
                  className="absolute inset-0 bg-gradient-to-br from-primary/10 to-transparent"
                  whileHover={{ scale: 1.05 }}
                  transition={{ duration: 0.5 }}
                />
                <div className="absolute inset-0 flex items-center justify-center">
                  <div className="text-center">
                    <motion.div 
                      className="text-6xl mb-4 text-primary/60"
                      whileHover={{ scale: 1.1, rotate: 5 }}
                      transition={{ duration: 0.3 }}
                    >
                      {project.icons[0]}
                    </motion.div>
                    <p className="text-secondary text-sm">Click to view details</p>
                  </div>
                </div>
              </div>

              {/* Project Content */}
              <div className="lg:w-1/2 p-8 flex flex-col justify-center">
                <motion.div
                  className="mb-4"
                  initial={{ opacity: 0, x: index % 2 === 0 ? 50 : -50 }}
                  whileInView={{ opacity: 1, x: 0 }}
                  viewport={{ once: false, amount: 0.3 }}
                  transition={{ duration: 0.6, delay: 0.2 }}
                >
                  <span className="text-primary/70 text-sm font-semibold tracking-wider uppercase">
                    {project.category}
                  </span>
                </motion.div>

                <motion.h3 
                  className="text-2xl lg:text-3xl font-bold text-primary mb-4 group-hover:text-white transition-colors"
                  initial={{ opacity: 0, x: index % 2 === 0 ? 50 : -50 }}
                  whileInView={{ opacity: 1, x: 0 }}
                  viewport={{ once: false, amount: 0.3 }}
                  transition={{ duration: 0.6, delay: 0.3 }}
                >
                  {project.title}
                </motion.h3>

                <motion.p 
                  className="text-secondary text-lg mb-6 leading-relaxed"
                  initial={{ opacity: 0, x: index % 2 === 0 ? 50 : -50 }}
                  whileInView={{ opacity: 1, x: 0 }}
                  viewport={{ once: false, amount: 0.3 }}
                  transition={{ duration: 0.6, delay: 0.4 }}
                >
                  {project.description}
                </motion.p>

                <motion.div 
                  className="flex flex-wrap gap-3 mb-6"
                  initial={{ opacity: 0, x: index % 2 === 0 ? 50 : -50 }}
                  whileInView={{ opacity: 1, x: 0 }}
                  viewport={{ once: false, amount: 0.3 }}
                  transition={{ duration: 0.6, delay: 0.5 }}
                >
                  {project.icons.slice(0, 4).map((icon, iconIndex) => (
                    <motion.div
                      key={iconIndex}
                      className="p-2 bg-primary/10 rounded-lg text-primary/70 hover:text-primary hover:bg-primary/20 transition-all duration-300"
                      whileHover={{ scale: 1.1, rotate: 5 }}
                    >
                      {icon}
                    </motion.div>
                  ))}
                </motion.div>

                <motion.div 
                  className="text-primary/70 text-sm font-medium flex items-center"
                  initial={{ opacity: 0, x: index % 2 === 0 ? 50 : -50 }}
                  whileInView={{ opacity: 1, x: 0 }}
                  viewport={{ once: false, amount: 0.3 }}
                  transition={{ duration: 0.6, delay: 0.6 }}
                >
                  <span className="mr-2">Click for details</span>
                  <motion.div
                    animate={{ x: [0, 5, 0] }}
                    transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
                  >
                    →
                  </motion.div>
                </motion.div>
              </div>
            </motion.div>
          ))}
        </div>
      </div>

      {/* Project Modal */}
      <AnimatePresence>
        {selectedProject && (
          <motion.div
            className="fixed inset-0 bg-black/80 backdrop-blur-sm z-50 flex items-center justify-center p-4"
            variants={overlayVariants}
            initial="hidden"
            animate="visible"
            exit="exit"
            onClick={() => setSelectedProject(null)}
          >
            <motion.div
              className="bg-gradient-to-br from-gray-800 to-gray-900 rounded-2xl max-w-4xl w-full max-h-[90vh] overflow-y-auto border border-primary/30"
              variants={modalVariants}
              onClick={(e) => e.stopPropagation()}
            >
              {/* Modal Header */}
              <div className="flex justify-between items-start p-8 border-b border-primary/20">
                <div>
                  <span className="text-primary/70 text-sm font-semibold tracking-wider uppercase">
                    {selectedProject.category}
                  </span>
                  <h3 className="text-3xl font-bold text-primary mt-2">
                    {selectedProject.title}
                  </h3>
                </div>
                <motion.button
                  onClick={() => setSelectedProject(null)}
                  className="text-secondary hover:text-primary transition-colors p-2"
                  whileHover={{ scale: 1.1, rotate: 90 }}
                  whileTap={{ scale: 0.95 }}
                >
                  <FaTimes size={24} />
                </motion.button>
              </div>

              {/* Modal Content */}
              <div className="p-8 space-y-8">
                <div>
                  <h4 className="text-xl font-semibold text-primary mb-4">About This Project</h4>
                  <p className="text-secondary text-lg leading-relaxed">
                    {selectedProject.fullDescription}
                  </p>
                </div>

                <div>
                  <h4 className="text-xl font-semibold text-primary mb-4">Key Features</h4>
                  <ul className="space-y-2">
                    {selectedProject.features.map((feature, index) => (
                      <motion.li
                        key={index}
                        className="flex items-center text-secondary"
                        initial={{ opacity: 0, x: -20 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ delay: index * 0.1 }}
                      >
                        <span className="text-primary mr-3">•</span>
                        {feature}
                      </motion.li>
                    ))}
                  </ul>
                </div>

                <div>
                  <h4 className="text-xl font-semibold text-primary mb-4">Technologies Used</h4>
                  <div className="flex flex-wrap gap-3">
                    {selectedProject.technologies.map((tech, index) => (
                      <motion.span
                        key={index}
                        className="px-4 py-2 bg-primary/10 text-primary rounded-lg text-sm font-medium"
                        initial={{ opacity: 0, scale: 0.8 }}
                        animate={{ opacity: 1, scale: 1 }}
                        transition={{ delay: index * 0.1 }}
                        whileHover={{ scale: 1.05 }}
                      >
                        {tech}
                      </motion.span>
                    ))}
                  </div>
                </div>

                <div className="flex flex-col sm:flex-row gap-4">
                  <motion.a
                    href={selectedProject.github}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="flex items-center justify-center px-6 py-3 bg-gray-700 hover:bg-gray-600 text-primary rounded-lg transition-colors font-medium"
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    <FaGithub className="mr-2" />
                    View Code
                  </motion.a>
                  <motion.a
                    href={selectedProject.demo}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="flex items-center justify-center px-6 py-3 bg-primary hover:bg-white text-dark rounded-lg transition-colors font-medium"
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    <FaExternalLinkAlt className="mr-2" />
                    Live Demo
                  </motion.a>
                </div>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.section>
  );
};

export default Projects;
